INSERT INTO teammate (league_id, player_id, paid)
SELECT t.league_id, t.player_id,
  CASE
    WHEN RANDOM() < 0.3 THEN NULL
    WHEN RANDOM() < 0.6 THEN FALSE
    ELSE TRUE
  END AS paid
FROM (
    SELECT l.id AS league_id, p.id AS player_id, ROW_NUMBER() OVER (PARTITION BY l.id ORDER BY RANDOM()) AS row_num
    FROM league l
    CROSS JOIN player p
    WHERE l.id BETWEEN 1 AND 15
      AND p.id BETWEEN 1 AND 40
) t
WHERE t.row_num <= FLOOR(RANDOM() * (15 - 5 + 1)) + 5;

